// 用户登录与信息相关service
window.APP.factory("UserService", ["$http", function($http) {
    var login = function(user) {
        $http({
            method: "POST",
            url: "/login",
            data: user
        }).success(function(resp) {
            if (resp && resp.data) {
                console.info("login success");
            }
        });
    };

    return {
        login: login
    };
}]);

// 用户是否登录service
// TODO 可重构为 UserService, PostService 可以进行用户登录检查
window.APP.factory("CheckLoginService", ["$q", "$http", "$rootScope", function($q, $http, $rootScope) {
    var checkLogin = function() {
        var defer = $q.defer();
        $http({
            method: "POST",
            url: "/checklogin"
        }).success(function(resp) {
            if (resp.error) {
                window.location.href = "/";
            } else {
                // 方便权限控制 FIXME 采用更好的方式
                $rootScope.user = resp.data; 

                defer.resolve(resp.data);
            }
        }).error(function(data) {
            window.location.href = "/";
        });
        return defer.promise;
    };
    var userPromise = checkLogin();

    return {
        checkLogin: checkLogin,
        userPromise: userPromise,
    };
}]);
// Confirm
window.APP.factory("ConfirmService", ["$rootScope", "$compile" ,function($rootScope, $compile) {
    var confirm=function(text,callback){
        var html = '<div m-Confirm></div>';
        $rootScope.mConfirmContent=text;
        $rootScope.confirmSure=callback;
        $body=$('body');
        $body.append($compile(html)($rootScope));
    };
    var close=function(){

            $('.confirm').remove();
    };
    return {
        confirm: confirm,
        close:close
    }
}]);
window.APP.factory("ValiService", ["$rootScope", "$compile" , "MessageService",function($rootScope, $compile,MessageService) {
    var Notempty=function(text){
        if(text){
            return true;
        }else{
            return false;
        }
    };
    var isUrl=function(text){
        var match = /^((ht|f)tps?):\/\/[\w\-]+(\.[\w\-]+)+([\w\-\.,@?^=%&:\/~\+#]*[\w\-\@?^=%&\/~\+#])?$/;
        if(match.test(text)){
            return true;
        }else{
            return false;
        }
    };
    var upIntNum=function(text){
        var match = /^[1-9]*[1-9][0-9]*$/;
        if(match.test(text)){
            return true;
        }else{
            return false;
        }
    };
    var  check=function(d){
        if(d&&d.error&&d.error.code=='unlogin'){
            MessageService.uniqueError("用户未登录，请前去登录！").then(function () {
                window.location.href = "/";
            });
        }
    };
    var isChinese=function(text){
        var match = /.*[\u4e00-\u9fa5].*/;
        if(match.test(text)){
            return true;
        }else{
            return false;
        }
    };
    return {
        Notempty: Notempty,
        isUrl:isUrl,
        upIntNum:upIntNum,
        check:check,
        isChinese:isChinese
    }
}]);
// message提示服务
window.APP.factory("MessageService", ["$rootScope", "$compile", "$q", "$timeout", "$ngConfirm", function($rootScope, $compile, $q, $timeout, $ngConfirm) {
    var $body = $('body');
    var defaultOptions = {
        showClose: false,
        message: "",
        duration: 3000, // 默认3秒钟, 0为永久
        type: 'info'
    };
    // base64图标
    var imgs = {
        success: "data:image/svg+xml;base64,",
        info: "data:image/svg+xml;base64,",
        warning: "data:image/svg+xml;base64,",
        error: "data:image/svg+xml;base64,"
    };
    var message = function(options) {
        var options = $.extend({}, defaultOptions, options);
        var imgSrc = imgs[options.type] || imgs.info;
        var html = '<div m-message m-img-src="' + imgSrc + '" m-message-content="' + options.message + '" m-message-close="' + options.showClose + '" m-duration="' + options.duration + '"></div>';
        $body.append($compile(html)($rootScope));
    };

    // 同时只能存在一条的信息
    var isBusy = false;
    var uniqueMessage = function (options) {
        if (isBusy) return;
        isBusy = true;
        
        message(options);

        var defer = $q.defer();
        $timeout(function() {
            isBusy = false;
            defer.resolve();
        }, 1000);
        return defer.promise;
    };

    var defaultConfirmOptions = {
        scope: $rootScope,
        icon: 'fa fa-warning'
    };
    var confirm = function (options) {
        options = $.extend({}, defaultConfirmOptions, options);
        $ngConfirm({
            boxWidth: "400px",
            animation: "",
            title: options.title,
            content: options.content,
            icon: options.icon,
            closeIcon: true,
            scope: options.scope,
            buttons: {
                cancel: {
                    text: "取消",
                    btnClass: "ng-confirm-btn-cancel",
                    action: options.cancel || angular.noop
                },
                confirm: {
                    text: "确定",
                    btnClass: "ng-confirm-btn-confirm",
                    action: options.confirm || angular.noop
                }
            }
        });
    };

    return {
        message: message,
        error: function(scope, msg) {
            return message({
                type: "error",
                message: msg,
                scope: scope
            });
        },
        success: function(scope, msg) {
            return message({
                type: "success",
                message: msg,
                scope: scope
            });
        },
        uniqueError: function (msg) {
            return uniqueMessage({
                type: "error",
                message: msg
            });
        },
        confirm: confirm   
    };
}]);

// download 服务
window.APP.factory("DownloadService", [function() {
    var download = function(url, data) {
        if (url && $.isPlainObject(data)) {
            var form = document.createElementNS("http://www.w3.org/1999/xhtml", "form");
            form.setAttribute("action", url);
            form.setAttribute("method", "post");
            document.body.appendChild(form);

            $.each(data, function(key, d) {
                var _c = document.createElementNS("http://www.w3.org/1999/xhtml", "input");
                _c.setAttribute("type", "hidden");
                _c.setAttribute("name", key);
                _c.setAttribute("value", d);
                form.appendChild(_c);
            });

            form.submit();
            document.body.removeChild(form);
        }
    };

    return {
        download: download
    };
}]);